import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AddRoutines, userInfo, deleteRoutines } from "../api";

const Routines = ({
  posts,
  setPosts,
  setUserdata,
  userdata,
  setToken,
  token,
}) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const history = useNavigate();

  const handleDelete = async (id, token) => {
    const response = await deleteRoutines(id, token);

    if (response.success) {
      fetchMyRoutines().then((posts) => {
        setPosts(posts);
      });
      history("/routines");
    }
  };

  const displayActivities = posts.map((posts) =>
    posts.activities.map((x) => x.name)
  );

  // const xitems = displayActivities.map((n) => "<li>" + n + "<i>");
  // const xhtml = "<ul>" + items.join("") + "</ul>";
  // console.log("xmap2", html);

  console.log("map", displayActivities);
  return (
    <>
      <div>
        {userdata && (
          <Link className="addpost" to="/add">
            ADD ROUTINES
          </Link>
        )}
      </div>
      <div className="routine-detail">
        {posts &&
          posts.map(({ name, goal, creatorName }) => {
            return (
              <>
                <div key={posts.creatorName}>
                  <h3>{name} </h3>
                  <h4> {goal}</h4>
                  <p>
                    <span className="creatorName"></span>
                    {creatorName}
                  </p>

                  {token && userdata.username === username && (
                    <>
                      <div>
                        <button
                          className="deletebtn"
                          onClick={() => handleDelete(_id, token)}
                        >
                          Delete Post
                        </button>
                      </div>

                      <div>
                        <button
                          className="updatebtn"
                          onClick={() => handleUpdate(_id)}
                        >
                          Update Post
                        </button>
                      </div>
                    </>
                  )}
                </div>
              </>
            );
          })}
      </div>
    </>
  );
};

export default Routines;
