import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { updateRoutines, fetchRoutines } from "../api";

const Update = ({ setRoutines, routines, setUserdata, userdata }) => {
  const [name, setName] = useState("");
  const [goal, setGoal] = useState("");
  const [isPublic, setIsPublic] = useState("");
  const history = useNavigate();
  const params = useParams();
  const routineId = params.id;
  console.log("params", params);
  const handleSubmit = async (event) => {
    event.preventDefault();
    const localSourcedToken = localStorage.getItem("token");
    console.log(isPublic);

    const response = await updateRoutines(
      localSourcedToken,
      name,
      goal,
      isPublic,
      routineId
    );

    if (response.success) {
      fetchRoutines().then((routines) => {
        setRoutines(routines);
      });
      history("/routines");
    }
  };

  const checkboxToggle = () => {
    document.getElementById("controlledCheckbox").checked = isPublic;
  };

  // useEffect(() => {
  //   // checkboxToggle();
  // }, [isPublic]);

  return (
    <div>
      {routines &&
        routines
          .filter((routines) => {
            if (userdata && userdata.id) {
              return routineId === routineId;
            }
            return false;
          })

          .map(({ name, goal, isPublic }) => {
            if (!name) setName(name);
            if (!goal) setGoal(goal);
            if (isPublic === null) setIsPublic(isPublic);
            // setIsPublic(isPublic);

            return (
              <form key="_id" onSubmit={handleSubmit} className="update-form">
                <div className="update-form-grp">
                  <label>name</label>
                  <input
                    type="text"
                    value={name}
                    id="name_input"
                    onChange={(event) => setName(event.target.value)}
                  />
                </div>

                <div className="update-form-grp">
                  <label>goal</label>
                  <textarea
                    type="text"
                    value={goal}
                    row="5"
                    id="goal_input"
                    onChange={(event) => setGoal(event.target.value)}
                  />
                </div>

                <div className="update-form-grp">
                  <div className="update-form-grp-cbox">
                    <input
                      id="controlledCheckbox"
                      type="checkbox"
                      checked={isPublic}
                      onChange={(event) => setIsPublic(event.target.checked)}
                    />

                    <span>Public?</span>
                  </div>
                </div>

                <button type="submit">Update Routine</button>
              </form>
            );
          })}
    </div>
  );
};

export default Update;
